mapper.select:
        SELECT SNO, ID, NAME, COST, BUYDATE, TRAINER, BUYCONTENT, PAYMENT
      FROM
      (

          SELECT
             /*+ INDEX_DESC(FITNESS_SALES PK_SALES) */
             ROWNUM RN, SNO, ID, NAME, COST, BUYDATE, TRAINER, BUYCONTENT, PAYMENT
         FROM 
            FITNESS_SALES
          WHERE 
          
             ROWNUM <= #{pageNum} * #{amount}
      )
      WHERE RN > (#{pageNum}-1) * #{amount}
